import React, {Component} from 'react';


class TwoWayBindingRef extends Component{
    constructor(props) {
        super(props);
        this.state = { valuedisplay: 0 };
        this.clickHandle=this.clickHandle.bind(this);
    }

    clickHandle(event){
        this.setState({
            valuedisplay: parseInt(this.refs.numberbox.value)
        });
    }

    render(){
        return (
            <div>
                <input type="data"  ref="numberbox" onChange={this.clickHandle} value={this.state.value} /> <br/><br/>
                <div>{this.state.valuedisplay}</div>
            </div>
        );
    }
}
export default TwoWayBindingRef ;
